updates.addNotification:
function (doc, req) {
    if(!doc){
        doc = {
            "_id":req.id,
            "notifications":[]
        }
    }
    var json = JSON.parse(req.body);
    var timestamp = parseInt(Date.now()/1000);
    doc.notifications.unshift({
        "user":json.user,
        "content":json.content,
        "url":json.link,
        "time":timestamp
        });;
    return [doc,"success"];
}

updates.removeAll:
function (doc, req) {
    if(!doc){
        return [null, '404'];
    }
    doc.notifications = [];
    return [doc,"success"];
}
