updates.changeName:
function (doc, req) { 
    if (!doc){
    	return [null, 'Not found']	
    }else{
    	if(doc.changed == 1){
    	    return [null, 'Already Changed'];
    	}
    	doc.changed = 1;
    	doc.username = req.query.username;
    	return [doc, 'Success'];
    }
}

updates.changeAvatar:
function (doc, req) { 
    if (!doc){
    	return [null, 'Not found']	
    }else{
    	doc.avatar = req.query.avatar;
    	return [doc, 'Success'];
    }
}
